import {useState, useEffect} from "react";

function useDebounce(value, timeout, callBackFn){
   const [timer,setTimer] = useState(null);
   
   function clearTimer(){
       if(timer){
           clearTimeout(timer);
       }
   }

   useEffect(()=>{
       clearTimer();

       if(value && callBackFn){
            const newTimer = setTimeout(callBackFn, timeout);
            setTimer(newTimer);
       }
   }, []);
   //[value]

}

export default useDebounce;
